INSERT INTO `equipment_data` VALUES (/*equipment_id*/101011, /*equipment_name*/"アイアンブレード", /*description*/"どこでも手に入る安価な剣。\n剣を学ぶ者の多くが\n初めて手にする大事な一振り。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/15.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101071, /*equipment_name*/"キラーペンシル", /*description*/"ペンの形をした短剣。\n攻撃力は低いが、\n携帯性に優れている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101101, /*equipment_name*/"ウッドグリップ", /*description*/"拳を強化する木製の武器。\n簡素な作りではあるが、素手で\n攻撃するよりも、拳にかかる\n負担を軽くすることができる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101131, /*equipment_name*/"狩人の弓", /*description*/"スタンダードな木製の弓。\n主に狩人が愛用しており、\n射程、威力共に、\n動物を狩るには申し分ない。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101161, /*equipment_name*/"鋼の槍", /*description*/"兵士がよく使用する槍。\nリーチが長く、扱いやすいため、\n戦での採用率が高い。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101191, /*equipment_name*/"片手斧", /*description*/"小型の片手斧。\n本来は投げ斧として作られたが、\n取り回しやすさ故に、\n近接武器としても使われている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101251, /*equipment_name*/"香木の杖", /*description*/"ミステリオン森林に群生している\nアストライアヒノキという\n香木から削り出した杖。\n香りが怪我人の気持ちを和らげる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101281, /*equipment_name*/"革のサロペット", /*description*/"もともとは職人のエプロン。\n大きなポケットがその名残。\n冒険初心者は持ち物が多いので\nこのポケットはとても重宝する。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/50.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101371, /*equipment_name*/"旅立ちのローブ", /*description*/"冒険者が最初に選ぶ一着。\n初めの一歩を踏み出すことへの\n祝福を込めて\nこの名で売られるようになった。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/55.0, /*atk*/0.0, /*magic_str*/1.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101401, /*equipment_name*/"木の盾", /*description*/"アストライアンシダーという\n針葉樹で作られた盾。\n加工しやすく安価なので\n学生の模擬戦によく用いられる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101431, /*equipment_name*/"革のブーツ", /*description*/"冒険者以外にも愛される定番の品。\n軽くて歩きやすく\n長く履くほどに足に馴染む。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101521, /*equipment_name*/"おしゃれなベレー", /*description*/"もとは絵描きが愛用していた帽子。\n日よけや風よけに手頃なため\n駆け出しの冒険者にも人気の品。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101551, /*equipment_name*/"やる気のブレスレット", /*description*/"装備すると、どこからかやる気が\n漲ってくる不思議なブレスレット。\n攻撃力を上げる効果がある。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101581, /*equipment_name*/"御守りペンダント", /*description*/"四つ葉のクローバーを\nあしらった御守り。\n幸運を呼び込み、持つ者の\n身を守ってくれると言われている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/50.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101611, /*equipment_name*/"気合のブレスレット", /*description*/"魔力を帯びたブレスレット。\nつけると気合いが漲り、\n装備者の魔法攻撃力を\n上げる効果がある。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102011, /*equipment_name*/"ギアブレード", /*description*/"刃と柄を歯車が繋ぐ珍しい剣。\n時計屋と鍛冶屋が\nコラボして開発した武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/30.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102013, /*equipment_name*/"ブルーブラッド", /*description*/"とある貴族に献上された名剣を\n模して作られた一品。\n見た目の美しさもさることながら、\n切れ味も良く、実用性も高い。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/60.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102014, /*equipment_name*/"オーロラの剣", /*description*/"七色に光る美しい剣。\n刀身は振るう度に発光し、\nその軌道は、まるで空中に\n光が舞うように見える。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102041, /*equipment_name*/"クレセントソード", /*description*/"美しい曲線を描く刀。\n勇猛で知られた将軍をして\n血に濡らすのは惜しいと言わしめた\n美術品としても一級の一振り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/35.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102071, /*equipment_name*/"コマンドナイフ", /*description*/"戦いより、森や山で遭難した時に\n真価を発揮するナイフ。\n生き残るための様々なギミックが\n詰め込まれている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102101, /*equipment_name*/"レザーナックル", /*description*/"革に鉄を埋め込んだ、\n拳を強化するための武器。\n己の体一つで戦おうとする\n武芸者の為の装備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102131, /*equipment_name*/"クリスタルボウ", /*description*/"クリスタルマウンテンで採れる\n素材を使って作られた弓。\n水晶が魔法の触媒にもなるため、\n魔力を帯びた矢を放つことが可能。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102162, /*equipment_name*/"トライデント", /*description*/"大陸を沈めるほどの力を持つ、\n海神ポセイドンの三叉鉾に\nあやかった槍。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/12.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102163, /*equipment_name*/"フレイムスピア", /*description*/"火の精霊の力を注いだ槍。\n揺らめく炎のような穂先は\n魔物を倒せば倒すほど\nますます激しく燃え盛っていく。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102191, /*equipment_name*/"メタルアックス", /*description*/"鋼の戦斧。\n木こり用の物と違い、\n武器として作られたそれは、\n確かな殺傷能力を持っている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/16.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102192, /*equipment_name*/"ハイメタルアックス", /*description*/"高度な鋳造技術を用いて\n作り出された戦斧。\nその重量を活かした攻撃で、\n敵を殲滅する。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102221, /*equipment_name*/"グリーンロッド", /*description*/"自然と密接な繋がりを持つ\nエルフが作り上げた杖。\n武器としてだけではなく、\n豊穣への祈りにも使われる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/0.0, /*magic_str*/16.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102222, /*equipment_name*/"リトルドラゴンロッド", /*description*/"宝玉を抱くドラゴンの\n姿をかたどった杖。\nわずかではあるが竜の力が\n込められている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/34.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102251, /*equipment_name*/"医者いらずの杖", /*description*/"回復魔法との相性が良い杖。\nヒーラーの魔法使いが\n好んで使用する。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102252, /*equipment_name*/"ユニコーンの慈愛", /*description*/"貴重なユニコーンの角を使った杖。\n角には傷を癒す力があり、\n回復魔法の効果が上がる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102281, /*equipment_name*/"智慧の衣", /*description*/"かつて、僧侶が信仰を広めるため\n大陸各地を旅する時に着た衣。\n動きやすく丈夫なため、\n多くの魔法使いが愛用している。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102282, /*equipment_name*/"ライトプレート", /*description*/"薄板で作られた鎧。\n確かな防御力と、軽さ故の\n動きやすさが特徴。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/320, /*require_level*/14, /*hp*/150.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102311, /*equipment_name*/"スケイルメイル", /*description*/"金属や革を鱗状に貼り付けた鎧。\n普通の鎧よりも動きやすく、\n斬撃や刺突に強い反面、\n鈍器などの打撃には弱い。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/95.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102312, /*equipment_name*/"ハイメタルプレート", /*description*/"高度な鋳造技術を用いて\n作り出された強固な鎧。\n並みの攻撃では、装備者に傷一つ\n負わせることはできない。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102341, /*equipment_name*/"フォークロアクロース", /*description*/"アストライアの伝統的な模様が\n織り込まれた服。\n購入には相応の身分や家格が必要。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102342, /*equipment_name*/"忍び装束", /*description*/"闇に紛れて行動するのに\nこれほど優れた衣装はない。\nどこかの国では、プロのスパイが\n愛用していたといわれている。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/110.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102371, /*equipment_name*/"春色ローブ", /*description*/"春を連想させる可愛らしいローブ。\n丈夫な作りで旅路にも向いており、\nそのデザインと実用性から\n愛用者は多い。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/65.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102372, /*equipment_name*/"ノルディックローブ", /*description*/"寒い北の国で愛用されていた。\n羊の毛から紡がれた糸は暖かく\nそれで織ったケープは肩や腕を\n厳しい風土から守ってくれる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102401, /*equipment_name*/"タワーシールド", /*description*/"身の丈ほどの金属の大盾。\n圧倒的な防御力を持つ反面、\nその重量故に、移動が困難となる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/15, /*hp*/135.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102431, /*equipment_name*/"クラウンブーツ", /*description*/"道化師をイメージして作られた\nおしゃれなブーツ。\n履くとどこか陽気な気分になる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/30.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102432, /*equipment_name*/"ハンティングブーツ", /*description*/"高山に生息する魔物の骨を使って\n作り上げられたブーツ。\nワイルドなデザインが、\n野生の心を呼び覚ます！", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/15, /*hp*/60.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102461, /*equipment_name*/"ウィッチブーツ", /*description*/"魔法薬の素材採取のために、\n各地を旅する魔法使いが履く靴。\n過酷な環境にも耐えられるように、\nとても丈夫に作られている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/9, /*hp*/30.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102491, /*equipment_name*/"かわいいキャスケット", /*description*/"ゴーグルがついたキャスケット。\n風船のようなシルエットが可愛いと\nランドソルのローティーン\n注目のアイテム。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102493, /*equipment_name*/"レギオンヘルム", /*description*/"古代の戦士たちが使用していた兜。\nとても頑丈に作られており、\n現代でも十分に使うことができる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102521, /*equipment_name*/"修道女のフード", /*description*/"修道女が使用するフード。\n彼女たちは、傷ついた人々の\n身も心も癒したという。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/16, /*hp*/0.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102551, /*equipment_name*/"勇気の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n勇を与えてくれる珠。\nどんな敵にもひるまず向かっていく\n勇気の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/30.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102552, /*equipment_name*/"スパイクバングル", /*description*/"魔物の持つ棘状の鱗がついた腕輪。\n装備者の力を上げる効果がある。\n凶暴な魔物の鱗で作られているため\nあまり市場には出回らない装備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102581, /*equipment_name*/"仁愛の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n仁を与えてくれる珠。\n自分や仲間を守りたいと願う\n愛の心の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/55.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102582, /*equipment_name*/"勝利のミサンガ", /*description*/"騎士が無事に帰還できるようにと、\n祈りを込めて作られたミサンガ。\nその思いが装備者を守り、\n防御力を上げてくれる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/17, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102611, /*equipment_name*/"マジックグラス", /*description*/"装備者の魔法攻撃力を\n上げてくれる特殊なメガネ。\n魔法石が素材として\n使われている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/11.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102612, /*equipment_name*/"智慧の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n智を与えてくれる珠。\n頭が冴え渡って魔法の効果が上がる\n知力の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102613, /*equipment_name*/"マジックモノクル", /*description*/"高貴な魔法使いが使用する片眼鏡。\n金属部分にはルーン文字が\n刻まれており、\n装備者の魔力を上げる力がある。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/17, /*hp*/0.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102614, /*equipment_name*/"文様の腕輪", /*description*/"銀の腕輪に、魔法石をあしらい、\n特殊な刻印を掘ったアクセサリー。\n魔法の流れをより活性化させ、\n装備者の魔力を上げる効果がある。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/16, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103011, /*equipment_name*/"煌めきの剣", /*description*/"隕石の欠片を使用した片刃の曲刀。\n刀身は光を浴びることで\n星屑のように煌めく。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/90.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103012, /*equipment_name*/"竜紋剣", /*description*/"竜の息吹が描かれた剣。\n幅広の刃は、突くことよりも\n斬ることに優れており、振るう姿は\n天を舞う竜のように見えるだろう。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/115.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103013, /*equipment_name*/"フェザーブレード", /*description*/"ある魔法使いが近接戦のために\n使用した剣。非力だった彼は、\n大きな鳥の羽に魔法をかけて、\nこの極めて軽量な剣を作り出した。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/37, /*hp*/135.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103041, /*equipment_name*/"月光刀", /*description*/"東国に存在するある剣術流派の\n免許皆伝者に与えられる刀剣。\n月光の力が宿っており、装備者の\n五感と霊感を研ぎ澄ませる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/3000, /*require_level*/30, /*hp*/65.0, /*atk*/58.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103042, /*equipment_name*/"雷神刀", /*description*/"ある凄腕の侍が、雷獣を退治\nしたとされている刀。\n雷獣を斬った刀身には、\n稲妻の力が宿っている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/40, /*hp*/75.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103071, /*equipment_name*/"プラチナナイフ", /*description*/"とても高価なプラチナを\n使用して作られた短剣。\n護身用に懐に忍ばせる貴族も多い。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103072, /*equipment_name*/"ヴァンパイアダガー", /*description*/"意思を持ち、血を求め続ける\n恐ろしい短剣。\n血を浴びると、錆びるどころか、\nより一層妖しく輝く。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103101, /*equipment_name*/"ドラゴンズクロー", /*description*/"凶悪な黒竜の顎をかたどった鉤爪。\n暗器の一種で、かつてこの鉤爪を\n使用していた暗殺者は、爪に毒を\n塗り、殺傷能力を高めたという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103131, /*equipment_name*/"キューピッドの弓", /*description*/"かつてこの弓で仕留めた獲物を\n想い人に贈り恋を叶えた男がいた。\n彼はその出来事にあやかり、\n愛の神の名をこの弓につけた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103132, /*equipment_name*/"デビルズボウ", /*description*/"人から迫害され、地下深くに\n追いやられた魔族の制作した弓。\n彼らは自分たちの領域に立ち入る\n人間を、この弓で葬ってきた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103161, /*equipment_name*/"騎士の誇り", /*description*/"名誉ある騎士に贈られる斧槍。\nその刃は、折れることなく、\n国と民の為に戦う騎士の誇りを\n体現している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/18, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103162, /*equipment_name*/"アブソリュートスピア", /*description*/"一突一殺の槍。\nその殺傷能力は、\n強固な鎧をいとも簡単に貫き、\n一突きで敵の息の根を止める。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/46.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103191, /*equipment_name*/"ブルースフィアアックス", /*description*/"青き天体をあしらった戦斧。\n世界そのものを象徴する天体は、\n大地のエネルギーを吸収し、\n装備者に力を与える。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103192, /*equipment_name*/"麗花の戦斧", /*description*/"華麗な花を施された\nその斧が振るわれるたびに、\n新たな鮮血の大輪が咲く。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103193, /*equipment_name*/"稲妻の斧", /*description*/"高度な武器の製造を研究する、\nある工房で制作された一挺。\n斧の内部に雷を貯蓄し、\n攻撃に際して放出することが可能。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/66, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103221, /*equipment_name*/"ソーンブラッドロッド", /*description*/"ある残忍な魔法使いの一団が\n使用していた杖。\nこの杖は、彼らの犠牲となった\n者たちの血で紅く染め上げられた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103222, /*equipment_name*/"デビルズホーン", /*description*/"ある魔法使いが、悪魔との取引で\n手に入れたとされる曰く付きの品。\n倒した敵の魂は、この杖を通して\n悪魔に献上されるという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103251, /*equipment_name*/"天球の見守り", /*description*/"占星術を生業とする、ある魔法使い\n一族が使用していた杖。\n彼らは星を観測し、\n未来を予知したという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103252, /*equipment_name*/"花蕾の杖", /*description*/"人を救いたいという想いが\nこの蕾を花開かせ、\nその清浄なエネルギーは、\n治癒の力となる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/0.0, /*atk*/0.0, /*magic_str*/53.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103281, /*equipment_name*/"ファッションアーマー", /*description*/"強くてオシャレな騎士をテーマに\n作られた鎧。ただし、どんなに\nオシャレをしても、実力が伴って\nいなければ魅力は半減してしまう。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103282, /*equipment_name*/"バトリングドレス", /*description*/"屈強な男たちを次々と倒し、\n闘技場の女王となった女戦士の鎧。\n対戦相手は彼女の獣のような眼光を\n見ただけで戦意を喪失したという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/42, /*sale_price*/3000, /*require_level*/32, /*hp*/325.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103283, /*equipment_name*/"ヘビーメタルアーマー", /*description*/"厚めの鉄板を組み合わせた鎧。\n軽快さを捨てて、防御力に\n特化させており、斧や大剣を使う\n戦士たちが好んで使用している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/380.0, /*atk*/17.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103311, /*equipment_name*/"ゴージャスアーマー", /*description*/"派手好きの騎士が、デザイナーに\n頼んで作らせた鎧。\n派手さ故に戦場で目立った彼は、\n敵にとっては恰好の的となった。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103341, /*equipment_name*/"ミルフィーユクロース", /*description*/"布地の上に革のローブを重ねた服。\n防寒対策に優れ、寒冷地での\n素材採取などに適している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/180.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/160.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103342, /*equipment_name*/"パレスクロース", /*description*/"王城内に務める女性騎士の服。\n上質な革と、黒金を使用し、\n胸にはナイトの地位を示す装飾。\n気品ある意匠に仕上がっている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/210.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103343, /*equipment_name*/"コスモスクロース", /*description*/"エルフの女王が、他種族との\n和平協定会議の際に着ていた服。\nエルフ族は万が一に備え、女王の\n服に強力な魔法のベールを張った。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/75, /*sale_price*/4000, /*require_level*/44, /*hp*/205.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/370.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103371, /*equipment_name*/"キューピッドのローブ", /*description*/"逸話に登場する愛の神の衣装を\nかたどって作られたローブ。\n背面の羽は、魔法で浮いており、\n本物と見間違えるほどの出来栄え。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103372, /*equipment_name*/"哲学者のローブ", /*description*/"幼くして皇帝の座についた少年を、\n生涯支え続けた哲学者の服。\n彼は皇帝に正しき道と教えを説き、\n国をより良い未来へと導いた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/245.0, /*atk*/0.0, /*magic_str*/14.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103401, /*equipment_name*/"ガーネットシールド", /*description*/"勇猛果敢な将軍の盾。\n彼は前線に立つと、\n自ら敵陣に切り込み、\n軍を勝利に導いた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/290.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103431, /*equipment_name*/"忍び足", /*description*/"履くと足音が消える特殊なブーツ。\n東国のスパイが履いていたらしいが\n彼らにまつわる情報は\nそのほとんどが謎のままだ。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/3.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103432, /*equipment_name*/"フェンサーブーツ", /*description*/"レイピアなどの細剣を使用する\n剣士のために作られたブーツ。\nとても軽く、力よりも機動性を\n重視する細剣士には最適の装備。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/3000, /*require_level*/35, /*hp*/115.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/4.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103461, /*equipment_name*/"ウィッチパレスブーツ", /*description*/"魔法使いが正装として履くブーツ。\nどんな場所を歩こうとも、決して\n汚れない魔法がかかっている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103491, /*equipment_name*/"ドラゴンヘッド", /*description*/"かつて存在した黒龍を信仰する\n騎士団が使用していた兜。\n彼らは龍に捧げる生贄のために、\n罪もない人々の命を奪い続けた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/35, /*hp*/235.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103521, /*equipment_name*/"ウィッチカチューシャ", /*description*/"魔法使いの一家が、修行のために\n家を出る娘に贈るカチューシャ。\n母親が自身の魔力を練り込み、\n愛をこめて作り上げる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103522, /*equipment_name*/"ウィッチハット", /*description*/"伝統的な魔法使いの帽子を、\n魔力を補助するアクセサリーで\n飾ったもの。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/26.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103551, /*equipment_name*/"オパールイヤリング", /*description*/"魔力の籠った\nオパールのイヤリング。\n装備者は体の内から活力が溢れ、\n攻撃力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/90.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103552, /*equipment_name*/"鷲獅子の羽飾り", /*description*/"鷲の上半身と、獅子の下半身を持つ\nグリフォンの羽で作られた羽飾り。\n獰猛な獣の力が宿っている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/235.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103553, /*equipment_name*/"スカーレットダイヤ", /*description*/"世にも珍しい緋色のダイヤ。\n竜の炎を封じ込めていると言われ、\n装備すると烈火の如き力を\n手に入れることができる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/70.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103581, /*equipment_name*/"エメラルドイヤリング", /*description*/"魔力の籠った\nエメラルドのイヤリング。\n装備者は不屈の精神を得て、\n防御力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103582, /*equipment_name*/"オクタグラムペンダント", /*description*/"八芒星は無限の循環を意味し、\n自然に存在する八つのエレメントを\n吸収し、装備者に\n絶え間なくその加護を授ける。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/37, /*hp*/0.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103583, /*equipment_name*/"獅子王の守護", /*description*/"かつて獅子王と呼ばれた王の品。\n生涯の大半を戦の中で\n過ごした彼は、その勇猛さから\n多くの民衆に畏れ敬われた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/46, /*hp*/270.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103611, /*equipment_name*/"ターコイズイヤリング", /*description*/"魔力の籠った\nターコイズのイヤリング。\n装備者は叡智を得て、\n魔法攻撃力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/30.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103612, /*equipment_name*/"パールイヤリング", /*description*/"魔力の籠った\nパールのイヤリング。\n装備者は精神集中力が増し、\n魔力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/36, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103613, /*equipment_name*/"ソーサラーグラス", /*description*/"賢人が龍脈の探索に使用する。\n大地に流れる気を研究し、\n大陸の環境や状態の調査に使う。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/41, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/0.0, /*magic_str*/39.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/35.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103614, /*equipment_name*/"月桂樹の哀しみ", /*description*/"精霊ダフネは自らの姿を月桂樹に\n変えた。彼女を愛したアポロンは\nその葉を永遠の愛の印として、\n身に着け続けたと言う。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104011, /*equipment_name*/"エンジェルブレード", /*description*/"大天使が使っていたとされる\n白銀の剣。\n邪悪な存在を消し去る\n退魔の力を待っている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/165.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104012, /*equipment_name*/"太陽剣", /*description*/"太陽神・アポロンの加護を受けたと\nされる剣。その剣を手にした人物は\n太陽王と呼ばれ、歴史に名を刻むと\n共に永きに渡る繁栄をもたらした。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/70.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104041, /*equipment_name*/"妖蝶の刀", /*description*/"深き森の遺跡に眠るという刀剣。\nこの剣に選ばれし者は、蝶に\n誘われることで、その場所に\n辿り着くことができるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/80.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104042, /*equipment_name*/"光輝の剣", /*description*/"光の女神・アグライアの神託を受け\nた刀鍛冶が生涯をかけて鍛え上げた\n光の剣。人類が魔神に対抗しうる武\n器として、高名な勇者が手にした。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/192, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/10.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104071, /*equipment_name*/"プレシャスナイフ", /*description*/"あるドワーフの名工が、人間の王に\n献上したと言われる短剣。\n使用された素材は、現存する\nどの鉱石とも一致しない未知の物。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104072, /*equipment_name*/"プリンセスの懐剣", /*description*/"歴史上最も長く存続した小国の王家\nに伝わる短刀。その刀は代々王女へ\nと受け継がれ、彼女らに降りかかる\n魔を払い、国の安寧を守り続けた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/50.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/300.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104101, /*equipment_name*/"火焔のガントレット", /*description*/"煉獄の焔で鍛え上げられた\n深紅の篭手。\n拳から発する業火は、\n眼前の敵を跡形もなく焼き払う。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/50, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104102, /*equipment_name*/"獅子王のガントレット", /*description*/"獅子王と呼ばれた名君が使用した\n黄金の篭手。その篭手は使用者に\n無限の力を与え、一騎当千の戦い\nを約束したといわれている。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/81.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/12.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104131, /*equipment_name*/"雷光弓", /*description*/"英雄は人を喰らう海竜を倒すために\nこの弓に雷の力を封じ込めた。\n電光纏いしその矢は、海竜を一撃で\n葬ったといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/145, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104132, /*equipment_name*/"エンジェルボウ", /*description*/"旧時代の終末大戦で大天使が悪魔\nとの戦いに用いた大弓。この放た\nれた矢の一撃は、地獄の最下部コ\nキュートスにまで届いたという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/65.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104161, /*equipment_name*/"緋竜槍", /*description*/"火山に生息する竜の背に、\n突き刺さっていた槍。\n穂先は長年竜の体内にあったことで\n炎の力を宿すこととなった。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104162, /*equipment_name*/"クイーンビースピア", /*description*/"大陸の全土を支配したといわれる伝\n説の女王が使用していた大槍。英知\nに優れ、戦にも長けていた彼女は、\nこの槍の力で大陸を手中に収めた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104191, /*equipment_name*/"紫龍斧", /*description*/"人の世を脅かした邪龍を、各地の\n英傑たちが集いこの斧に封印した。\n龍の魂が宿ったこの斧は、\n禍々しいエネルギーを発している。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104192, /*equipment_name*/"ヘルファイアアックス", /*description*/"地獄の業火が封じられた両刃斧。そ\nの身は血のように赤く、ひと振りす\nれば紅蓮の炎が敵に襲いかかり、骨\nまで焼き尽くすといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/97.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104221, /*equipment_name*/"フューリーロッド", /*description*/"妻を殺され、復讐の鬼と化した\n魔法使いが所持していた杖。\n彼は自身の怒りをこの杖に籠め、\n妻の死に関わった全員を抹殺した。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/0.0, /*magic_str*/91.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104222, /*equipment_name*/"太陽のロッド", /*description*/"太古の賢人たちが太陽の欠片をより\n集めて作ったとされる神秘的な杖。\nその杖に選ばれた者は全知全能の力\nを身に宿すといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104251, /*equipment_name*/"月華杖", /*description*/"月の女神が、信仰深い信者の\n病を治す際に使用したとされる杖。\n彼女の力を恐れた死の神は、\n杖を下界に隠したといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/0.0, /*magic_str*/73.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104252, /*equipment_name*/"生命のロッド", /*description*/"生命の樹と呼ばれる巨大樹の枝で作\nられた奇跡の杖。振るえば枯れ木に\n新緑の葉が芽吹き、大地を突けば水\nが湧くといわれている。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/67.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104281, /*equipment_name*/"インビジブルドレス", /*description*/"姿を消す魔法がかかった特殊な鎧。\nある国の姫が許されざる恋をし、\n駆け落ちの際に使用したと\n言われている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104282, /*equipment_name*/"ミスリルプレート", /*description*/"銀の輝きと鋼をしのぐ強さを\nあわせ持つ希少な金属と、\n当代随一の名工との出会いが生んだ\n美と強靭さを兼ね備えた至高の鎧。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/172, /*sale_price*/10000, /*require_level*/55, /*hp*/250.0, /*atk*/25.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104311, /*equipment_name*/"エンジェルアーマー", /*description*/"天使たちが戦で使用した鎧。\n低級な悪魔は鎧に近づくことすら\nできず、白銀に反射する光を\n浴びるだけで、消滅したという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104312, /*equipment_name*/"バーミリオンメイル", /*description*/"一騎駆けにその身を捧げた\n狂戦士の魂が宿る鎧。\nこれを身に着けた者は\n戦鬼の力を得る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/10000, /*require_level*/55, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/400.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104341, /*equipment_name*/"月下の法衣", /*description*/"王の密命を受けた神官が己の命を\n賭して作り上げた美しき法衣。\n月明かりの中で縫い上げたため、\n月光を受けると七色の輝きを放つ。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/190, /*sale_price*/7200, /*require_level*/51, /*hp*/360.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/325.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104342, /*equipment_name*/"ハーミットクロース", /*description*/"隠者と呼ばれる、魔道の深淵を\n覗かんとした者の服。\n魔力が練り込まれた糸で\n縫製されている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/10000, /*require_level*/55, /*hp*/180.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104371, /*equipment_name*/"大魔導師のローブ", /*description*/"人生を魔道に捧げた大魔導師が\nまとっていたローブ。\nかつての主の強大な力により、\n未だ妖しい魔力を放ち続けている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104372, /*equipment_name*/"聖者のローブ", /*description*/"天使たちと共に、\n悪魔と対峙した聖者が\nまとったとされるローブ。\nその気高さは神話の領域。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/141, /*sale_price*/10000, /*require_level*/55, /*hp*/240.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104401, /*equipment_name*/"エンプレスシールド", /*description*/"女王が絶対の信頼を置く\n近衛兵にのみ与えられる盾。\n絶対的な守護の力を宿しており、\n魔法や呪いすらも跳ね返す。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/166, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/4.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104431, /*equipment_name*/"パラディングリーブ", /*description*/"神聖な力による加護を受けた\n聖騎士の脚具。\n彼らは民の命を守るため、\n命を懸けて魔物と戦った。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/9.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/6.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104432, /*equipment_name*/"エンジェルブーツ", /*description*/"神話に姿を現す聖なる脚具。\nあらゆる攻撃を寄せ付けず、\n炎の中すら涼しげに\n歩くことができる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/140, /*sale_price*/10000, /*require_level*/56, /*hp*/220.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104461, /*equipment_name*/"ウィザードブーツ", /*description*/"王族に認められた魔法使いにのみ\n贈られる特別な品。\nその道百年のエルフの職人にしか、\nこの靴を作ることができない。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104491, /*equipment_name*/"フェザーワルツ", /*description*/"空の踊り手と呼ばれた半神半人の\n騎士が使用していた兜。\n彼は空を縦横無尽に舞い、\n敵を翻弄したと言われている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/135, /*sale_price*/7200, /*require_level*/52, /*hp*/490.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104521, /*equipment_name*/"ウィザードフード", /*description*/"国が認める最高位の魔法使いの証。\nこのフードを持つ者は、\n王の傍らに立つことを許される。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/0.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104522, /*equipment_name*/"法王のフード", /*description*/"歴代の法王が\n外征時に身につけた頭具。\n破魔の効果が付与されており、\n幻術を造作もなく見破る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/10000, /*require_level*/56, /*hp*/90.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104551, /*equipment_name*/"エレメンタルハート", /*description*/"四大精霊の魔力を統合し、\n封じ込めた特殊な鉱石。\n稀代の錬金術師が成し遂げた\n一大発明品。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/58, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104581, /*equipment_name*/"身代わりのバングル", /*description*/"長きに渡り君臨した皇帝が\n作らせたという伝説の魔道具。\n装備者に降りかかる厄災を\n肩代わりするという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/58, /*hp*/240.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/5.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0);
