INSERT INTO `hatsune_description` VALUES (/*id*/2, /*event_id*/10001, /*type*/1, /*description*/"イベントクエストを進めるとボスが登場します。\nボスには、ボスチケットを使うことで挑めます。\nボス撃破で、討伐証を大量にゲットできるので、果敢に挑戦しましょう。");
INSERT INTO `hatsune_description` VALUES (/*id*/3, /*event_id*/10001, /*type*/1, /*description*/"討伐証交換は、報酬ゲットのチャンスです。\n報酬の中には、イベントに登場するキャラのメモリーピースもあります。\nたくさん集めてキャラを仲間にしましょう。");
