INSERT INTO `room_item_detail` VALUES (/*room_item_id*/143, /*level*/1, /*item_detail*/"カフェにありそうなメニューボード。マーカーやチョークで彩られた黒板に書かれるのは、ほとんど『本日のおやつ』メニュー。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/144, /*level*/1, /*item_detail*/"『雲海の山脈』を制覇した証。山頂付近にのみ出現する貴重な魔物の肉。最高級の霜降りで、ほっぺたが落ちそうな美味しさ。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/145, /*level*/1, /*item_detail*/"『密林の大樹』を制覇した証。通称『世界一おいしいフルーツ』。刃を容易に通さないほど硬い外皮を持つが、その果肉は絶品。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/146, /*level*/1, /*item_detail*/"『断崖の遺跡』を制覇した証。遺跡で手に入れたレシピで作った点心セット。希少な肉や野菜などをふんだんに使った至高の料理。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/148, /*level*/1, /*item_detail*/"羽根がみっちりと詰まったふかふかのクッション。お部屋にアクセントを加えるビビットなピンク色のカバーがポイント！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/149, /*level*/1, /*item_detail*/"パーテーションがわりにも使える、ドレッシーなカーテン。高品質な桃色の布地は見た目はもちろん手触りも素晴らしく、ずっと触れていたくなってしまう心地良さ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/150, /*level*/1, /*item_detail*/"ホワイトとイエローの２色で塗り分けられたカォンの柱。どのようなジャンルの家具にも馴染むシンプルなデザインをしている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/151, /*level*/1, /*item_detail*/"マホの厳選チョイスにより選び抜かれた、とびきりのフワフワぬいぐるみがいっぱい。眺めて幸せ、触って幸せ、抱きしめて幸せの三拍子が揃った夢の楽園。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/152, /*level*/1, /*item_detail*/"団員たちの武器が入った樽。急な出立の際も素早く得物を手に外へ出ることができる。ときどき武器同士が絡まって樽ごと倒れてしまうのがたまにきず。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/153, /*level*/1, /*item_detail*/"絶対開けるなよ！　とマコトに念を押されている、いわく付きの宝箱。とても重くマコトにしか持ち上げられない。様々な噂があるが実際なにが入っているのかは、彼女しか知らない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/154, /*level*/1, /*item_detail*/"ぼんやりと淡い光が部屋全体を包み込む、癒やしオーラばっちりのランプ。明るすぎず暗すぎない光が、任務で疲れた身体と心をメルヘンチックな気分にいざなってくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/155, /*level*/1, /*item_detail*/"メルヘンチックな雰囲気が漂うカォンの壁。これに囲まれればおとぎ話の登場人物のような気分になり、どこでもそこはマホマホ王国。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/156, /*level*/1, /*item_detail*/"ネイティブ柄のラグやマットが敷き詰められた床。エスニックな雰囲気とカラフルな配色から個性的な印象を受ける。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/157, /*level*/1, /*item_detail*/"ホワイトとピンクの色彩がかわいらしいキュートな螺旋階段。その階段を上れば、まるでお姫様気分を体験することができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/158, /*level*/1, /*item_detail*/"下階へと下りるためのキュートな螺旋階段。長く伸びた柱の一部はくり抜かれ、ろうそくを立てるためのキャンドルホルダーとなっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
