INSERT INTO `unit_comments` VALUES (/*id*/1001002, /*unit_id*/100101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"困った時はお互いさまさま！\n何かあったら相談してね！\n出来ること、あるはずだから！");
