INSERT INTO `unit_comments` VALUES (/*id*/1001004, /*unit_id*/100101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/6.0, /*change_face*/2, /*description*/"実はね…？　一緒にいると\nすっごく楽しいんだ！\nえへへ…ありがとね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1001005, /*unit_id*/100101, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.0, /*change_face*/6, /*description*/"なんだろ…時々\n胸の真ん中が熱くなるんだ\nもしかして風邪引いちゃった！？");
INSERT INTO `unit_comments` VALUES (/*id*/1001006, /*unit_id*/100131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"強くなるんだ…！\nあたしはみんなと…\nずっと笑顔でいたい！");
INSERT INTO `unit_comments` VALUES (/*id*/1001007, /*unit_id*/100131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"傷だらけになっても大丈夫！\n支えてくれる人がいるから…\n何度でも立ち上がってみせる！");
INSERT INTO `unit_comments` VALUES (/*id*/1001008, /*unit_id*/100131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンが近くにいてくれたら\n元気100万倍って感じ！\n…ちょっと言い過ぎかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1001009, /*unit_id*/100131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"聞こえるよ…騎士くんの声\nどんなに離れてても、\nあたしには分かる！");
INSERT INTO `unit_comments` VALUES (/*id*/1001010, /*unit_id*/100131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえどんな人が相手でも、\n今は負ける気がしない…！\n支えてくれるあの人がいるから！");
INSERT INTO `unit_comments` VALUES (/*id*/1002001, /*unit_id*/100201, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/6.0, /*change_face*/5, /*description*/"あ、あの…\nわたしが話し相手で、\n本当にいいの？\n…そっか。ありがとう…！");
INSERT INTO `unit_comments` VALUES (/*id*/1002002, /*unit_id*/100201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.0, /*change_face*/5, /*description*/"戦うなら、\n治療と援護はまかせて！\n攻撃はその、一緒に\n頑張って欲しい…かな");
INSERT INTO `unit_comments` VALUES (/*id*/1002003, /*unit_id*/100201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/4.0, /*change_face*/8, /*description*/"お願い…\nそんなにじっと見ないで…\nは、恥ずかしいよ…あうぅ…");
