INSERT INTO `unit_comments` VALUES (/*id*/1003004, /*unit_id*/100301, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/2.0, /*change_face*/2, /*description*/"何故だろう…\nキミと剣を交えていると\n踊っているみたいだ");
