INSERT INTO `unit_comments` VALUES (/*id*/1004001, /*unit_id*/100401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"あっ、にいちゃんだー！\nねえねえ、\nミソギとなにして遊ぶ？");
INSERT INTO `unit_comments` VALUES (/*id*/1004002, /*unit_id*/100401, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/4.0, /*change_face*/4, /*description*/"う～イタズラ用の\n激辛キャンディー、\n自分で食べちゃったよぉ…");
INSERT INTO `unit_comments` VALUES (/*id*/1004003, /*unit_id*/100401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.0, /*change_face*/6, /*description*/"よーし、イタズラの設置完了！\nにいちゃんをひっかけて…\nあっ…もしかして、\n見ちゃった？");
INSERT INTO `unit_comments` VALUES (/*id*/1004004, /*unit_id*/100401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"ミソギがイタズラを用意する…\nにいちゃんが引っかかる…\nうんっ、最高のコンビだね！");
