INSERT INTO `unit_comments` VALUES (/*id*/1004006, /*unit_id*/100431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミソギ探検隊、全員集合！\nミソギとにいちゃんの二人だけ\nなんだけどね");
INSERT INTO `unit_comments` VALUES (/*id*/1004007, /*unit_id*/100431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これからも、いっぱい\nにいちゃんで遊べるよね！\n…あ、にいちゃんと遊べる、\nって言いたかったんだよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1004008, /*unit_id*/100431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃーん！\n早く早くっ探検に行こっ！\nにいちゃんのこと\n待ってたんだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1004009, /*unit_id*/100431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大好きなにいちゃんのために、\nたっくさんイタズラ\n用意しなくっちゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1004010, /*unit_id*/100431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミソギねぇ、本当の\nにいちゃんが出来たみたいで、\nすっごくうれしいんだー♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006001, /*unit_id*/100601, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/3.5, /*change_face*/2, /*description*/"お兄ちゃん、\nまたあとでサックスの練習、\n付き合ってくれますか？");
