INSERT INTO `unit_comments` VALUES (/*id*/1010006, /*unit_id*/101031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『くるりんぱっ☆　』あららぁ、\nここ洞窟やってんなあ\nでもうちの魔法で明るうなったし\nそれにとってもきれいですやろ？");
INSERT INTO `unit_comments` VALUES (/*id*/1010007, /*unit_id*/101031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え、手ぇ引いてくれはるん？　\nおおきに、嬉しおす…\nほなうちは王子はんの足元明るう\n照らして、一緒に歩きまひょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1010008, /*unit_id*/101031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はんの道筋はうちが\n照らします～\nせやから、王子はんは安心して\n一緒にいておくれやす～");
INSERT INTO `unit_comments` VALUES (/*id*/1010009, /*unit_id*/101031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はんとならどこにいても\nお伽噺話の世界になるわ～\nさあ王子はん、マホ姫を連れて\n探検に参りまひょ～♪");
