INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204000, /*unit_name*/"プチグリフォン", /*prefab_id*/204000, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n翼が小さく、まだ飛べない\nこどものグリフォン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204200, /*unit_name*/"アルトバード", /*prefab_id*/204200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/450, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【回復】範囲回復\n癒しの歌声が遠くまでよく届く\nトリ魔物界の有名歌手。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204300, /*unit_name*/"ポイズンポッド", /*prefab_id*/204300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】範囲攻撃\n大きく開いた口に溜めた\n溶解液で獲物を食らう。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204400, /*unit_name*/"プチデビル", /*prefab_id*/204400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/0, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻撃\n覚えたての魔法で手当り次第に\n攻撃する赤ちゃん悪魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"リザードマン", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】二刀を自在に操り、\n煙幕弾で相手の目を\nくらませる\nオオトカゲの戦士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"クリーパー", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】頭から放つ毒攻撃と\nツタによる範囲物理攻撃で\n近づく冒険者の体力を一気に奪う\n悪魔の花。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"オークリーダー", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】族長として群れを鼓舞し\n大地を砕くほどの連撃で\n敵に大ダメージを与える\nオークの中のオーク。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"エルダーホーン", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ひとたび暴れ出せば\n手が付けられず、その嘶きは\n天まで届くと言われる\n伝説のワイルドホーン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"エルダータイラント", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な頭突きで地を砕き\n全体に大ダメージを与える。\n身体や甲羅が巨大に進化した\n岩山のようなタートルタイラント。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"シードレイク", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】強力な氷のブレスで\n前衛を一瞬で氷漬けにし、\n咬みついて身体を砕く\n獰猛な深海竜。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"サベージベア", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"グレーターゴーレム", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"ウールヴヘジン", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠吠えで獲物を麻痺させ\nひるんだ所を爪で引き裂く。\n数々の冒険者の行く手を阻んできた\n残虐を極める魔界の狼。");
