INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203800, /*unit_name*/"オーク", /*prefab_id*/203800, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n倒した冒険者の鎧をまとい\n斧を振り回す凶暴な森の獣人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203801, /*unit_name*/"ハイオーク", /*prefab_id*/203801, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n厳しい鍛錬を重ね、強靭な肉体を\n手に入れたオークの上位種。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203900, /*unit_name*/"イビルキャノン", /*prefab_id*/203900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n長年使われるうちに\n悪魔の魂が宿った、命ある砲台。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204000, /*unit_name*/"プチグリフォン", /*prefab_id*/204000, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n翼が小さく、まだ飛べない\nこどものグリフォン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204200, /*unit_name*/"アルトバード", /*prefab_id*/204200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/450, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【回復】範囲回復\n癒しの歌声が遠くまでよく届く\nトリ魔物界の有名歌手。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204300, /*unit_name*/"ポイズンポッド", /*prefab_id*/204300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n大きく開いた口に溜めた\n溶解液で獲物を食らう。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204400, /*unit_name*/"プチデビル", /*prefab_id*/204400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻撃\n覚えたての魔法で手当り次第に\n攻撃する赤ちゃん悪魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204500, /*unit_name*/"ハンマーキャンサー", /*prefab_id*/204500, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n大きな爪と固い殻で、どんな大きな\n相手にも向かっていく、カニの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204600, /*unit_name*/"マジックレイ", /*prefab_id*/204600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】範囲攻撃\n頭部の石から発する超音波で\n巧みに狩りをするエイの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204700, /*unit_name*/"リーフボア", /*prefab_id*/204700, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n固い葉っぱを鎧のように纏い、\n痛烈な体当たりをする大イノシシ。\nだが所詮葉っぱ。魔法は防げない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204701, /*unit_name*/"サンドボア", /*prefab_id*/204701, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n砂を固めて鎧のように纏うのは、\n捨て身を恐れない一族の戦士の証。\nだが所詮は砂。魔法は防げない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205200, /*unit_name*/"タツノコナイト", /*prefab_id*/205200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n海で溺れた騎士の槍を奪い、\n縄張りを警備するタツノコの戦士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205300, /*unit_name*/"アイスチック", /*prefab_id*/205300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻撃\n氷を纏う尾羽根を飛ばして\n攻撃するトリの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205500, /*unit_name*/"ランドスネーク", /*prefab_id*/205500, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n穴を掘って獲物をじっと待ち、\n毒牙で確実に仕留めるヘビの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"ミノタウロス", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】迷宮に巣食う、牡牛座の魔星獣。\n巨大な戦斧を振り回して、冒険者の命を奪う。\n\n・敵全体に物理大ダメージを与え、火傷状態\n　にする。\n・敵1キャラに物理大ダメージを与え、ノック\n　バックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法中ダメージを与え、\n　麻痺させる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/"メサルティム", /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】夢魔の館に住む、牡羊座の魔星獣。\n冒険者を夢世界に束縛し、魂を喰らう。\n\n・敵全体に魔法大ダメージを与え、束縛状態\n　の敵にはさらに魔法小ダメージを与える。\n・前方範囲内の敵に魔法中ダメージを与える。\n・敵1キャラを束縛状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"リザードマン", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】二刀を自在に操り、\n煙幕弾で相手の目を\nくらませる\nオオトカゲの戦士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"クリーパー", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】頭から放つ毒攻撃と\nツタによる範囲物理攻撃で\n近づく冒険者の体力を一気に奪う\n悪魔の花。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300601, /*unit_name*/"ニードルクリーパー", /*prefab_id*/300601, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　麻痺状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300602, /*unit_name*/"ニードルクリーパー", /*prefab_id*/300602, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　麻痺状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"オークリーダー", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】族長として群れを鼓舞し\n大地を砕くほどの連撃で\n敵に大ダメージを与える\nオークの中のオーク。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"オークチーフ", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したオークリーダー。\n巨大な盾で、攻防一体の一撃を繰り出す。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方範囲内の敵の防御力を大幅に下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"エルダーホーン", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ひとたび暴れ出せば\n手が付けられず、その嘶きは\n天まで届くと言われる\n伝説のワイルドホーン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"スピリットホーン", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したエルダーホーン。\n角に魔力を込め、打撃より強力な一撃を放つ。\n\n・前方範囲内の敵に魔法大ダメージを与え、\n　ノックバックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法小ダメージを与え、\n　魔法防御と攻撃速度を小ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"エルダータイラント", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な頭突きで地を砕き\n全体に大ダメージを与える。\n身体や甲羅が巨大に進化した\n岩山のようなタートルタイラント。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"シードレイク", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】強力な氷のブレスで\n前衛を一瞬で氷漬けにし、\n咬みついて身体を砕く\n獰猛な深海竜。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"サベージベア", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"マッドベア", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方範囲内の敵に物理小ダメージを与え、\n　物理防御力を中ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"グレーターゴーレム", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"ウールヴヘジン", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠吠えで獲物を麻痺させ\nひるんだ所を爪で引き裂く。\n数々の冒険者の行く手を阻んできた\n残虐を極める魔界の狼。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301600, /*unit_name*/"ジズ", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大HPに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301601, /*unit_name*/"ジズ", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大HPに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大HPに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のHPが50％を切ると、さらに\n　攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301900, /*unit_name*/"ウェアオルカ", /*prefab_id*/301900, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302000, /*unit_name*/"ワイルドグリフォン", /*prefab_id*/302000, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】過酷な環境で進化したプチグリフォン。竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
